INSERT INTO `AUTHORS` VALUES (1,'Adonis','1930-01-01','adonis@hotmail.com','Born to a modest Alawite farming family[6] in January 1930, Adonis hails from the village of al-Qassabin near the city of Latakia in western Syria. He was unable to afford formal schooling for most of his childhood, and his early education consisted of learning the Quran in the local kuttab (mosque-affiliated school) and memorizing classical Arabic poetry, to which his father had introduced him.','12345'),(2,'Leila Fuad Aboulela','1964-05-12','leilafuad@hotmail.com','Leila Fuad Aboulela s a fiction writer, essayist, and playwright of Sudanese origin based in Aberdeen, Scotland.[1] She grew up in Khartoum, Sudan, and moved to Scotland in 1990 where she began her literary career. Until 2023, Aboulela has published six novels and several short stories, which have been translated into fifteen languages','auth2'),(7,'khalil','2022-12-31','khalil4@hotmail.com','Born in lebanon.','Kh@$1243554345'),(8,'Unknown','1970-01-01','default@email.com',NULL,NULL),(10,'Denise Mina','1970-01-01','denisemina@unknown.com',NULL,NULL),(11,'John Abercrombie','1970-01-01','johnabercrombie@unknown.com',NULL,NULL),(12,'Matthew Lipman','1970-01-01','matthewlipman@unknown.com',NULL,NULL),(13,'Ann Margaret Sharp','1970-01-01','annmargaretsharp@unknown.com',NULL,NULL),(14,'Frederick S. Oscanyan','1970-01-01','fredericks.oscanyan@unknown.com',NULL,NULL),(15,'Henry Smith Williams','1970-01-01','henrysmithwilliams@unknown.com',NULL,NULL),(16,'Mary Morton Wood','1970-01-01','marymortonwood@unknown.com',NULL,NULL),(17,'Evelyn Arizpe','1970-01-01','evelynarizpe@unknown.com',NULL,NULL),(18,'Vivienne Smith','1970-01-01','viviennesmith@unknown.com',NULL,NULL),(19,'Wolfgang MENZEL (Literary Historian.)','1970-01-01','wolfgangmenzel(literaryhistorian.)@unknown.com',NULL,NULL),(20,'William B. Bonvillian','1970-01-01','williamb.bonvillian@unknown.com',NULL,NULL),(21,'Erika Gottlieb','1970-01-01','erikagottlieb@unknown.com',NULL,NULL),